mapper.select:

	select	reserveDateNo,
			themeNo,
			reserveDate
    from reserveDate
    where themeNo = #{themeNo}
    and reserveDate = #{reserveDate}

mapper.update:

update reserveTime
set reserveState = #{reserveState}
where reserveTimeNo = #{reserveTimeNo}


update reserve
set paymentState = 2
where reserveNo = #{reserveNo}
