mapper.update:


update cafe
set cafeImg = #{cafeImg}

where cafeNo = #{no}


update users
set userType = 2
where userNo = #{userNo}

mapper.select:

select img
from INTERIORIMG
where cafeNo = #{cafeNo}



	select cafeNo, cafeName, cafeHp, openTime, closeTime, 
		   cafeIntro, cafeImg, printAddress, sidoDetail
	from cafe








					where sido = #{sidoDetail}




							where sidoDetail = #{sidoDetail}








order by cafeNo desc

select rt.rn, rt.cafeNo, rt.cafeName, rt.cafeHp, rt.openTime, rt.closeTime, rt.cafeIntro, rt.cafeImg, rt.printAddress, rt.sidoDetail from ( select rownum rn, ot.cafeNo, ot.cafeName, ot.cafeHp, ot.openTime, ot.closeTime, ot.cafeIntro, ot.cafeImg, ot.printAddress, ot.sidoDetail from( select cafeNo, cafeName, cafeHp, openTime, closeTime, cafeIntro, cafeImg, printAddress, sidoDetail from cafe
order by cafeNo desc ) ot ) rt
where rn >= #{startNum} and rn <= #{cnt}

select rt.rn, rt.cafeNo, rt.cafeName, rt.cafeHp, rt.openTime, rt.closeTime, rt.cafeIntro, rt.cafeImg, rt.printAddress, rt.sidoDetail from ( select rownum rn, ot.cafeNo, ot.cafeName, ot.cafeHp, ot.openTime, ot.closeTime, ot.cafeIntro, ot.cafeImg, ot.printAddress, ot.sidoDetail from( select cafeNo, cafeName, cafeHp, openTime, closeTime, cafeIntro, cafeImg, printAddress, sidoDetail from cafe
where sido = #{region}
where sidoDetail = #{region}
order by cafeNo desc ) ot ) rt
where rn >= #{startNum} and rn <= #{cnt}



select min(pMin)minPMin, max(pMax) maxPMax
from theme
where cafeNo = #{cafeNo}



select p.price
from themePrice p,theme th
where th.themeNo = p.themeNo
and th.themeNo = #{themeNo}
and p.headCount = #{headCount}
