mapper.select:
select rt.rn, rt.partyNo, rt.userNo, rt.reservedateno, rt.reserveDate, rt.cafeno, rt.themeno, rt.themetime, rt.reservePerson, rt.content, rt.partyState from ( select rownum rn, ot.partyNo, ot.userNo, ot.reservedateno, ot.reserveDate, ot.cafeno, ot.themeno, ot.themetime, ot.reservePerson, ot.content, ot.partyState from ( select p.partyNo, p.userNo, p.reservedateno, to_char(reservedate, 'YY-MM-DD') reserveDate, p.cafeno, p.themeno, p.themetime, p.reserveperson, p.content, p.partyState from party p , partyDetail pd where p.partyNo = pd.partyno and pd.userno = #{userNo}
and pd.userstate = '1'
and pd.userstate = '2'
order by p.partyNo desc ) ot ) rt where rn >= #{startRnum} and rn <= #{endRnum}

select count(*) from party p , partyDetail pd where p.partyNo = pd.partyno and pd.userno = #{userNo}
and pd.userstate = '1'
and pd.userstate = '2'


select  userNo
from users


where id = #{id}
