mapper.select:

	select	reserveDateNo,
			themeNo,
			reserveDate
    from reserveDate
    where themeNo = #{themeNo}
    and reserveDate = #{reserveDate}

mapper.update:

update reserveTime
set reserveState = #{reserveState}
where reserveTimeNo = #{reserveTimeNo}

mapper.select:
select rt.rn, rt.themename, rt.reserveno, rt.reserveDate, rt.reservetime, rt.reserveperson, rt.reservename, rt.paymentstate, rt.themeNo from ( select rownum rn, ot.themename, ot.reserveno, ot.reserveDate, ot.reservetime, ot.reserveperson, ot.reservename, ot.paymentstate, ot.themeNo from ( select t.themename, r.reserveno, to_char(rd.reservedate, 'yyyy-mm-dd') reserveDate, rt.reservetime, r.reserveperson, r.reservename, r.paymentstate, t.themeNo from theme t, reserveDate rd, reserveTime rt, reserve r where t.themeNo = rd.themeNo and rd.reserveDateNo = rt.reservedateno and rt.reservetimeNo = r.reservetimeno
and t.themeNo = #{themeNo}
and rd.reserveDate = #{reserveDate}
order by rd.reserveDate desc, rt.reserveTime desc ) ot ) rt where rn >= #{startRnum} and rn <= #{endRnum}

select count(*) from theme t, reserveDate rd, reserveTime rt, reserve r where t.themeNo = rd.themeNo and rd.reserveDateNo = rt.reservedateno and rt.reservetimeNo = r.reservetimeno
and t.themeNo = #{themeNo}
and rd.reserveDate = #{reserveDate}

mapper.update:

update reserve
set paymentState = 2
where reserveNo = #{reserveNo}
