mapper.select:
select rt.rn, rt.reviewNo, rt.reviewTitle, rt.nickname, rt.reviewhit, rt.reviewLike, rt.reviewregdate, rt.userno, rt.reviewImg, rt.rating from (select rownum rn, ot.reviewNo, ot.reviewTitle, ot.nickname, ot.reviewhit, ot.reviewLike, ot.reviewregdate, ot.userno, ot.reviewImg, ot.rating from (select r.reviewNo, r.reviewTitle, u.nickname, r.reviewhit, r.reviewLike, to_char(r.reviewregdate, 'yy-mm-dd') reviewregdate, r.userno, r.reviewImg, r.rating from review r, users u where r.userNo = u.userNo
and r.reviewTitle || u.nickname like '%' || #{keyword} ||'%'
order by r.reviewNo desc)ot)rt where rn >= #{startRnum} and rn <= #{endRnum}

select rt.rn, rt.reviewNo, rt.reviewTitle, rt.nickname, rt.reviewhit, rt.reviewLike, rt.reviewregdate, rt.userno, rt.reviewImg, rt.rating from (select rownum rn, ot.reviewNo, ot.reviewTitle, ot.nickname, ot.reviewhit, ot.reviewLike, ot.reviewregdate, ot.userno, ot.reviewImg, ot.rating from (select r.reviewNo, r.reviewTitle, u.nickname, r.reviewhit, r.reviewLike, to_char(r.reviewregdate, 'yy-mm-dd') reviewregdate, r.userno, r.reviewImg, r.rating from review r, users u where r.userNo = u.userNo
and r.reviewTitle || u.nickname like '%' || #{keyword} ||'%'
order by r.reviewLike desc)ot)rt where rn >= #{startRnum} and rn <= #{endRnum}

select rt.rn, rt.reviewNo, rt.reviewTitle, rt.nickname, rt.reviewhit, rt.reviewLike, rt.reviewregdate, rt.userno, rt.reviewImg, rt.rating from (select rownum rn, ot.reviewNo, ot.reviewTitle, ot.nickname, ot.reviewhit, ot.reviewLike, ot.reviewregdate, ot.userno, ot.reviewImg, ot.rating from (select r.reviewNo, r.reviewTitle, u.nickname, r.reviewhit, r.reviewLike, to_char(r.reviewregdate, 'yy-mm-dd') reviewregdate, r.userno, r.reviewImg, r.rating from review r, users u where r.userNo = u.userNo
and r.reviewTitle || u.nickname like '%' || #{keyword} ||'%'
order by r.reviewHit desc)ot)rt where rn >= #{startRnum} and rn <= #{endRnum}

mapper.update:

	update review
	set reviewhit = reviewhit+1
	where reviewno = #{reviewno}

update review
set  reviewTitle = #{reviewTitle}
    ,reviewContent = #{reviewContent}
    ,themeNo = #{themeNo}
    ,feelLevel = #{feelLevel}
    ,rating = #{rating}
    ,recTime = #{recTime}
    ,reviewHints = #{reviewHints}
    ,reviewClear = #{reviewClear}
    ,memberNum = #{memberNum}
    ,reviewImg = #{reviewImg}
where reviewNo = #{reviewNo}
and userNo= #{userNo}

update review
set  reviewTitle = #{reviewTitle}
    ,reviewContent = #{reviewContent}
    ,themeNo = #{themeNo}
    ,feelLevel = #{feelLevel}
    ,rating = #{rating}
    ,recTime = #{recTime}
    ,reviewHints = #{reviewHints}
    ,reviewClear = #{reviewClear}
    ,memberNum = #{memberNum}
where reviewNo = #{reviewNo}
and userNo= #{userNo}


	update review
	set reviewLike = reviewLike+1
	where reviewno = #{reviewNo}


	update review
	set reviewLike = reviewLike-1
	where reviewno = #{reviewNo}

mapper.insert:


	select commentNo.nextval from dual



	insert into reviewcomment
	values ( #{commentNo} 
			,#{reviewNo}
			,#{userNo}
			,#{commentContent}
			,sysdate
		   )

mapper.select:
select rt.rn, rt.boardNo, rt.boardtitle, rt.boardcontent, rt.regdate, rt.boardhit, rt.boardlike, rt.boardimg, rt.userno, rt.nickname from (select rownum rn, ot.boardNo, ot.boardtitle, ot.boardcontent, ot.regdate, ot.boardhit, ot.boardlike, ot.boardimg, ot.nickname, ot.userno from (select b.boardNo, b.boardtitle, b.boardcontent, to_char(b.regdate, 'yy-mm-dd') as regdate, b.boardHit, b.boardlike, b.boardimg, u.nickname, b.userno from freeboard b, users u where b.userNo = u.userNo
and b.boardTitle || u.nickname like '%' || #{keyword} ||'%'
order by b.boardNo desc)ot)rt where rn >= #{startRnum} and rn <= #{endRnum}

select rt.rn, rt.boardNo, rt.boardtitle, rt.boardcontent, rt.regdate, rt.boardhit, rt.boardlike, rt.boardimg, rt.userno, rt.nickname from (select rownum rn, ot.boardNo, ot.boardtitle, ot.boardcontent, ot.regdate, ot.boardhit, ot.boardlike, ot.boardimg, ot.nickname, ot.userno from (select b.boardNo, b.boardtitle, b.boardcontent, to_char(b.regdate, 'yy-mm-dd') as regdate, b.boardHit, b.boardlike, b.boardimg, u.nickname, b.userno from freeboard b, users u where b.userNo = u.userNo
